const inputEmail = $items("extractingdata")[0]?.json?.email?.toLowerCase();

if (!inputEmail) {
  throw new Error("‚ùå Email not found in 'extractingdata' node");
}

// Get all contacts from HubSpot 'get' node
const hubspotContacts = $items("get");

for (let contact of hubspotContacts) {
  const properties = contact.json.properties;

  if (!Array.isArray(properties)) continue;

  for (let prop of properties) {
    if (prop?.type === "EMAIL") {
      const contactEmail = prop?.value?.toLowerCase();

      if (contactEmail === inputEmail) {
        return [
          {
            json: {
              leadExists: true,
              matchedContactId: contact.json.id || null,
              matchedContact: contact.json
            }
          }
        ];
      }
    }
  }
}

// No match found
return [
  {
    json: {
      leadExists: false
    }
  }
];

const outputString = $json["output"];

const nameMatch = outputString.match(/Name:\s*(.+)/);
const emailMatch = outputString.match(/Email:\s*([^\s]+)/);
const mobileMatch = outputString.match(/Mobile:\s*([\d.\-]+)/);

const name = nameMatch ? nameMatch[1].trim() : null;
const email = emailMatch ? emailMatch[1].trim() : null;
const mobile = mobileMatch ? mobileMatch[1].trim() : null;

return [
  {
    json: {
      name,
      email,
      mobile
    }
  }
];

