const invoiceId = $json.inoviceid;
const amount = $json["amount total"];
const pdf = $json.pdf;
const status = $json.status;

// If any of the critical fields are missing ‚Üí return static message
if (!invoiceId || !amount || !status || !pdf) {
  return [{
    json: {
      message: "Invoice number not found. Please try again."
    }
  }];
}

// Otherwise, build the formatted message
const lines = [
  "Thanks for the info! Below are the invoice details:",
  "",
  `Invoice ID: ${invoiceId}`,
  "",
  `Amount Total: $${amount}`,
  "",
  `Status: ${status}`,
  "",
  `Invoice Link: ${pdf}`
];

return [{
  json: {
    message: lines.join('\n')
  }
}];
